import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Container, Row, Col, Nav, NavItem, NavLink, TabContent, TabPane } from "reactstrap";

const Home = () => {
  const [activeTab, setActiveTab] = useState("1");
  const dispatch = useDispatch();
  const data = useSelector(state => state.data);

  const toggle = tab => {
    if(activeTab !== tab) setActiveTab(tab);
  };

  return (
    <Container fluid>
      <Row>
        <Col xs="3">
          <Nav pills vertical>
            <NavItem>
              <NavLink
                className={activeTab === "1" ? "active" : ""}
                onClick={() => { toggle("1"); }}
              >
                Section 1
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink
                className={activeTab === "2" ? "active" : ""}
                onClick={() => { toggle("2"); }}
              >
                Section 2
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink
                className={activeTab === "3" ? "active" : ""}
                onClick={() => { toggle("3"); }}
              >
                Section 3
              </NavLink>
            </NavItem>
          </Nav>
        </Col>
        <Col xs="9">
          <TabContent activeTab={activeTab}>
            <TabPane tabId="1">
              <Section1 />
            </TabPane>
            <TabPane tabId="2">
              <Section2 />
            </TabPane>
            <TabPane tabId="3">
              <Section3 />
            </TabPane>
          </TabContent>
        </Col>
      </Row>
    </Container>
  );
};

const Section1 = () => {
  return <p>This is section 1.</p>;
};

const Section2 = () => {
  return <p>This is section 2.</p>;
};

const Section3 = () => {
  return <p>This is section 3.</p>;
};

export default App;
