import React from "react";
// import { Card, Button } from "react-bootstrap";
import redux from "../image/redux.svg";
import dp from "../image/react.png";
import { Col, Row } from "react-bootstrap";
import "..Css/FeatureCard.css";

function Features() {
  return (
    <>
      <Row className="justify-content-center d-inline-flex"
      //  style={{textAlign:'center'}}
       >
        <Col className="lg-4 md-8 sm-12">
          <FeaturesCard
            Img={dp}
            Title={"React"}
            Text={"React provides state-of-the-art functionality and is an excellent choice for developers looking for an easy-to-use and highly productive JavaScript framework."}
            // Using React, you can build complex UI interactions that communicate with the server in record time with JavaScript-driven pages.
            // color={"baby pink"}
            Link={"https://reactjs.org/docs/getting-started.html"}
          />
        </Col>
        <Col  className="lg-4 md-8 sm-12">
          <FeaturesCard
            Img={redux}
            Title={"Redux"}
            Text={"Redux allows you to manage your app's state in a single place and keep changes in your app more predictable and traceable. "}
            // color={"baby pink"}
            Link={"https://redux.js.org/"}
          />
        </Col>
      </Row>
    </>
  );
}

export default Features;
