import { Container, Navbar, Nav, NavLink, Button, FormGroup } from "react-bootstrap";
import feature from "../image/features.ico";
import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { clear } from "../redux/action/CounterAction";
import { clearStore } from "../redux/action/LoginAction";
import chat from "../image/chat-heart-fill.ico";
// import { Sidebar } from "react-pro-sidebar";
import SideBar from "./SideBar";

//
// import * as React from 'react';
// import AppBar from '@mui/material/AppBar';
// import Box from '@mui/material/Box';
// import Toolbar from '@mui/material/Toolbar';
// import Typography from '@mui/material/Typography';
// import IconButton from '@mui/material/IconButton';
// import MenuIcon from '@mui/icons-material/Menu';
// import AccountCircle from '@mui/icons-material/AccountCircle';
// import Switch from '@mui/material/Switch';
// import FormControlLabel from '@mui/material/FormControlLabel';
// import FormGroup from '@mui/material/FormGroup';
// import MenuItem from '@mui/material/MenuItem';
// import Menu from '@mui/material/Menu';


const Header = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  let isvalid = useSelector((state) => state.rootReducer.LoginReducer.isvalid);
  const [loggedIn, setLoggedIn] = useState(true);
  const clearLocalStorage = () => {
    localStorage.clear();
    dispatch(clear());
    dispatch(clearStore());
  };

//
const [auth, setAuth] = React.useState(true);
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleChange = (event) => {
    setAuth(event.target.checked);
  };

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };


  return (
    <div>
      <div>

      </div>
      {/* <Navbar bg="warning" variant="">
        <Container>
          <Navbar.Brand href="#home">Navbar</Navbar.Brand>
          <Nav className="me-auto">
            <Nav.Link href="/home" className="mx-1">
              <i className="fa fa-fw fa-home" />
              Home
            </Nav.Link>
            <Nav.Link href="/calculator" className="mx-1">
              <i className="fa fa-fw fa-calculator" />
              &nbsp;calculator
            </Nav.Link>
            <Nav.Link href="/features">
              <img src={feature} style={{ width: "30px", height: "25px" }} />
              &nbsp;Features
            </Nav.Link>
            <Nav.Link href="/about_Us">
              <i className="fa fa-fw fa-solid fa-info" />
              About Us
            </Nav.Link>
          </Nav>
          {isvalid === null ? (
            <Button
              className="justify-content-right d-flex"
              onClick={() => navigate("/login")}
              variant="outline-warning"
            >
              Log in
            </Button>
          ) : (
            <Button
              className="justify-content-right d-flex"
              onClick={clearLocalStorage}
              variant="warning"
            >
              Log out
            </Button>
          )}
        </Container>
      </Navbar> */}
      {/* <div id="app" style={({ height: "100vh" }, { display: "flex" })}>
        <SideBar/>
    </div> */}
{/*  */}

    <Box sx={{ flexGrow: 1 }}>
      <FormGroup>
        <FormControlLabel
          control={
            <Switch
              checked={auth}
              onChange={handleChange}
              aria-label="login switch"
            />
          }
          label={isvalid ? 'Logout' : 'Login'}
        />
      </FormGroup>
      <AppBar position="static">
        <Toolbar>
          <IconButton
            size="large"
            edge="start"
            color="inherit"
            aria-label="menu"
            sx={{ mr: 2 }}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Photos
          </Typography>
          {auth && (
            <div>
              <IconButton
                size="large"
                aria-label="account of current user"
                aria-controls="menu-appbar"
                aria-haspopup="true"
                onClick={handleMenu}
                color="inherit"
              >
                <AccountCircle />
              </IconButton>
              <Menu
                id="menu-appbar"
                anchorEl={anchorEl}
                anchorOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                keepMounted
                transformOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                open={Boolean(anchorEl)}
                onClose={handleClose}
              >
                <MenuItem onClick={handleClose}>Profile</MenuItem>
                <MenuItem onClick={handleClose}>My account</MenuItem>
              </Menu>
            </div>
          )}
        </Toolbar>
      </AppBar>
    </Box>
 </div>
  );
};
export default Header;
