import { Container, Navbar, Nav, Button } from "react-bootstrap";
import feature from '../image/feature.ico'
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { clear } from "../redux/action/CounterAction";
import { clearStore } from "../redux/action/LoginAction";

const Header= ()=>{
  const dispatch=useDispatch();
    const navigate = useNavigate();
    let isvalid = useSelector((state) => state.rootReducer.LoginReducer.isvalid);
    const [loggedIn, setLoggedIn ] = useState(true)
    const clearLocalStorage = () => {
      localStorage.clear();
      dispatch(clear());
      dispatch(clearStore());
    }
    return(
        <div>
            <Navbar bg="primary" variant="dark">
          <Container>
            <Navbar.Brand href="#home">Navbar</Navbar.Brand>
            <Nav className="me-auto">
              <Nav.Link href="/home" className="mx-3"><i className="fa fa-fw fa-home">Home</i></Nav.Link>
              <Nav.Link href="/calculator" className="mx-3"><i className="fa fa-fw fa-calculator">&nbsp;calculator</i></Nav.Link>
              <Nav.Link href="/features" className="mx-5"><img src={feature}/>Features</Nav.Link>
              <Nav.Link href="/about_Us" className="mx-1">About Us</Nav.Link>
            </Nav>
            { isvalid === null ? (
            <Button className="justify-content-right d-flex" 
            onClick={()=> navigate('/login')} 
            variant="outline-warning">
              Log in</Button>
               ) : ( 
            <Button className="justify-content-right d-flex"
            onClick={clearLocalStorage}
            variant="warning">Log out</Button>
            )    
              } 

            </Container>
        </Navbar>
        </div>
    )
}
export default Header
