import { Container, Navbar, Nav, Button } from "react-bootstrap";
import feature from '../image/features.ico'
import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { clear } from "../redux/action/CounterAction";
import { clearStore } from "../redux/action/LoginAction";
import chat from '../image/chat-heart-fill.ico'

const Header= ()=>{
  const dispatch=useDispatch();
    const navigate = useNavigate();
    let isvalid = useSelector((state) => state.rootReducer.LoginReducer.isvalid);
    const [loggedIn, setLoggedIn ] = useState(true)
    const clearLocalStorage = () => {
      localStorage.clear();
      dispatch(clear());
      dispatch(clearStore());
    }
    return(
        <div>
            <Navbar bg="warning" variant="">
          <Container>
            {/* <Link to={} */}
            <Navbar.Brand href="#home">Navbar</Navbar.Brand>
            <Nav className="me-auto">
              <Nav.link href="/home" className="mx-3"><i className="fa fa-fw fa-home">Home</i></Nav.link>
              <Nav.Link href="/calculator" className="mx-3"><i className="fa fa-fw fa-calculator">&nbsp;calculator</i></Nav.Link>
              <Nav.Link to="/features" className="mx-5"><img src={feature} style={{width:'30px',height:'25px'}}/>&nbsp;Features</Nav.Link>
              <Nav.Link href="/about_Us" className="mx-1"><img src={chat}/>About Us</Nav.Link>
            </Nav>
            { isvalid === null ? (
            <Button className="justify-content-right d-flex" 
            onClick={()=> navigate('/login')} 
            variant="outline-warning">
              Log in</Button>
               ) : ( 
            <Button className="justify-content-right d-flex"
            onClick={clearLocalStorage}
            variant="warning">Log out</Button>
            )    
              } 

            </Container>
        </Navbar>
        </div>
    )
}
export default Header
